import React, { useState } from "react";

const LoginForm = ({ onLogin }) => { // Recibimos la función onLogin como prop
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      // Cambié la URL para que coincida con la del backend
      const response = await fetch("http://localhost:3000/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error || "Error desconocido");
      } else {
        // Llamamos a la función onLogin pasándole el token y el rol
        onLogin(data.token, data.rol);
        alert("Inicio de sesión exitoso");
        console.log("Token:", data.token);
      }
    } catch (error) {
      setError("Error al conectar con el servidor");
    }
  };

  const containerStyle = {
    margin: "0",
    padding: "0",
    height: "100vh", // Altura completa de la pantalla
    width: "100vw",  // Anchura completa de la pantalla
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: "url('/images/imagen1login.webp')", // Ruta relativa desde public/
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };

  const formStyle = {
    background: "rgba(255, 255, 255, 0.8)", // Fondo blanco con transparencia
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)", // Sombra ligera
    textAlign: "center",
  };

  const errorStyle = {
    color: "red",
  };

  return (
    <div style={containerStyle}>
      <form style={formStyle} onSubmit={handleSubmit}>
        <h2>Iniciar Sesión</h2>
        {error && <p style={errorStyle}>{error}</p>}
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <br />
        <label htmlFor="password">Contraseña:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
