import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import LoginForm from "./components/LoginForm";
import Sidebar from "./components/Sidebar";
import Dashboard from "./pages/Dashboard";
import Usuarios from "./pages/Usuarios";

function App() {
  // Estado para manejar la autenticación del usuario y sus datos
  const [user, setUser] = useState({
    isAuthenticated: false,
    token: null,
    rol: null,
  });

  // Función para manejar el login
  const handleLogin = (token, rol) => {
    localStorage.setItem("user", JSON.stringify({ token, rol })); // Guardamos todo en un objeto
    setUser({
      isAuthenticated: true,
      token,
      rol,
    });
  };

  // Función para verificar si el usuario está autenticado
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { token, rol } = JSON.parse(storedUser);
      setUser({
        isAuthenticated: true,
        token,
        rol,
      });
    }
  }, []);

  return (
    <Router>
      {!user.isAuthenticated ? (
        <LoginForm onLogin={handleLogin} />
      ) : (
        <div style={{ display: "flex" }}>
          <Sidebar rol={user.rol} />
          <div style={{ flex: 1 }}>
            <Routes>
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/usuarios" element={<Usuarios />} />
              {/* Redirige a Dashboard si no se encuentra la ruta */}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
          </div>
        </div>
      )}
    </Router>
  );
}

export default App;
