import React, { useState } from "react";

const LoginForm = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch("http://localhost:3000/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error || "Error desconocido");
      } else {
        onLogin(data.token, data.rol);
        alert("Inicio de sesión exitoso");
        console.log("Token:", data.token);
      }
    } catch (error) {
      setError("Error al conectar con el servidor");
    }
  };

  const containerStyle = {
    margin: "0",
    padding: "0",
    height: "100vh",
    width: "100vw",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: "url('/images/imagen1login.webp')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    overflow: "hidden",
  };

  const formStyle = {
    background: "rgba(255, 255, 255, 0.9)",
    padding: "40px",
    borderRadius: "15px",
    boxShadow: "0 8px 12px rgba(0, 0, 0, 0.3)",
    textAlign: "center",
    width: "400px", // Aumentamos el ancho del formulario
    fontSize: "18px", // Tamaño de fuente más grande
  };

  const inputStyle = {
    width: "90%", // Input más ancho
    padding: "12px", // Espaciado interno más grande
    margin: "10px 0", // Espaciado entre los inputs
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  };

  const buttonStyle = {
    padding: "12px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "16px",
    width: "100%", // Botón que ocupa todo el ancho del formulario
  };

  const errorStyle = {
    color: "red",
  };

  return (
    <div style={containerStyle}>
      <form style={formStyle} onSubmit={handleSubmit}>
        <h2>Iniciar Sesión</h2>
        {error && <p style={errorStyle}>{error}</p>}
        <input
          style={inputStyle}
          type="email"
          id="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          style={inputStyle}
          type="password"
          id="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button style={buttonStyle} type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
